insert into note (uri,valeur,mime_type,file_id,source_id) values ('d3309a5b63209a3b', E'The cell bodies of neurons that bring sensory information from the skin, muscles, and joints lie in the dorsal root ganglia, clusters of cells that lie adjacent to the spinal cord. The axons of these neurons are bifurcated into peripheral and central branches. The central branch enters the dorsal portion of the spinal cord.', 'null', 'null', (SELECT id from note_source where uri = '9f9a616c1e9c3771'));
