insert into note (uri,valeur,mime_type,file_id,source_id) values ('6faed4ee9e009bcc', E'Fatty acids are stored in the cytoplasm of many cells in the form of droplets of triacylglycerol molecules, which consist of three fatty acid chains joined to a glycerol molecul', 'null', 'null', (SELECT id from note_source where uri = '802914d00820b82c'));
