insert into note (uri,valeur,mime_type,file_id,source_id) values ('e2d6ba736622eb5c', E'Lipides visibles\n----------\n- mono : arachide, olive, colza, noisette, sésame, maïs\n- w6 : tournesol, germes de blé, pépins de raisins de noix, huile de mais, soja\n- w3 : huile de noix colza, soja, germes de blé\nLipides cachés\n-------------------------\n- mono : oeufs, avocat, cacahuète, noisette pistache\n- w6 : amande noix germes de blé\n-w3 : noix germes de blé graines de lin', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('696c7a0930da1ce3', E'ATP is synthesized in an energetically unfavorable phosphorylation reaction in which a phosphate group is added to ADP (adenosine diphosphate). When required, ATP gives up its energy packet through its energetically favorable hydrolysis to ADP and inorganic phosphate. The regenerated ADP is then available to be used for another round of the phosphorylation reaction that forms ATP', 'null', 'null', (SELECT id from note_source where uri = '802914d00820b82c'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('8b5ae5f305e8f5f8', E'La glycolyse intervient dans le cytosol des cellules.  Elle produit de l''ATP sans atome d''oxygène. Durant la glycolyse une molécule de glucose à 6 carbones est convertie en 2 molécules de pyruvate à 3 carbones chacune. Pour chaque molécule de glucose, 2 molécules d''ATP sont hydrolysées pour fournir l''énergie nécessaire aux premières étapes mais 4 molécules d''ATP sont produites dans les dernières phases.', 'null', 'null', (SELECT id from note_source where uri = '802914d00820b82c'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('2752eba162352ce4', E'L''acide phytique diminue l''absorption des cations bivalents (CA, Mg, Fe, Cu, Zn 2+...)', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('c55c4e211949a01a', E'La dépense énergétique spécifique liée à l''utilisation cellulaire de chaque catégorie de nutriments est nommée action dynamique spécifique des nutriments (ADS) ou extra-chaleur postprandiale (ECPP) car elle se manifeste par une perte de chaleur vers le milieu extérieur suite à la prise alimentaire.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('f94551276a0f2764', E'Supplémenter les repas contenant du soja (cuit de préférence) par des aliments contenant de la méthionine permet de faire passer le CEP du soja de 1,3 à 2,8.\nLes aliments riches en méthionine sont :\n- Oeuf\n- Graines et farine de sésame\n- Graines de tournesol\n- Noix\n- Riz\n- Certaines épices\n- Avoine et son d''avoine\n- Blé dur', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('9eb29c24e8d5f289', E'Je nomme transcendantale toute connaissance qui s''occupe en générale moins d''objets que de notre mode de connaissance des objets, en tant que celui-ci doit être possible a priori.', 'null', 'null', (SELECT id from note_source where uri = '7d8ddd2f5d2edcd6'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('1bb5bd7a149b1976', E'Denis Sassou-Nguesso [...] dirige le Congo [...] depuis plus de deux décennies. Après avoir été battu lors de la première élection présidentielle libre, en 1992, il reconquiert le pouvoir par les armes avec le soutien de la France et de l''Angola 5 ans plus tard, à l''issue d''une guerre meurtière.', 'null', 'null', (SELECT id from note_source where uri = 'd472bd0b3ebe9582'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('498725cee680fdf6', E'Les acides aminés synthétisables par l''organisme sont :\n- acide aspartique\n- asparagine\n- acide glutamique\n- glutamine\n- proline\n- sérine\n- glycine\n- alanine\n- arginine', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('e0fe81334abe5e28', E'9 acides aminés sont dits indispensables du fait de leur synthèse :\n- incomplète (valine, isoleucine, leucine, histidine, tryptophane, méthionine, phénylalanine)\n- impossible (lysine et thréonine)', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('f1e9a22937917dce', E'On trouve les protéines végétales dans les céréales et les légumineuses principalement.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('78506ef29552c2ea', E'Les aliments sources de protéines végétales apportent en général :\n- fibres\n- vitamines B (sauf B12)\n- vitamine C\n- béta-carotène', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('bb22c099cb904089', E'Les protéines du lait ou de la viande présentent un léger déficit en acides aminés soufrés (méthionine et cystéine).\nLes céréales manquent totalement de lysine.\nLes légumineuses sont déficitaires en méthionine.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('c94f299531eab100', E'Les acides gras sont définis par la formule CH3-(CH2)n-COOH', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('07cc720788d40578', E'Les protéines doivent constituer entre 10 et 15% de l''apport énergétique total', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('6b07d75d2310704b', E'Les acides gras mono-insaturés sont synthétisés par l''organisme humain et entrent dans la composition des sphingolipides et des triglycérides de réserve.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('896f613c089b9a1a', E'Les HDL transporte le cholestérol des tissus vers le foie. Les LDL transportent le cholestérol du foie vers les cellules utilisatrices', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('432e444cf1cf5480', E'Les phytostérols sont de source exclusivement végétale. Elles sont faiblement absorbées par l''intestin mais inhibe l''assimilation du cholestérol puisque leurs absorptions sont compétitives. Cependant les aliments non enrichis en ont une teneur assez faible.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('5db10646917e85b1', E'Plus un corps gras est pauvre en double liaisons, plus il est concret à température ambiante. Plus un corps gras est riche en double liaisons plus il est fluide à température ambiant', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('33e684346888692d', E'Lorsque la chaîne aliphatique d''un acide gras est courte (moins de 6 C) le comportement hydrophile l''emporte du nez molécule devient soluble en milieu aqueux', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('74ecddfb2e37fcaa', E'Les AG trans sont plus solides à température ambiante, ce qui est une propriété recherchée par l''industrie agroalimentaire, notamment pour la préparation de margarines. Ils diminuent également la fluidité membranaire s''ils sont intégrés dans la bicouche lipidiqu', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('2baeb07e63e3c78d', E'Les lipides doivent représenter 30 à 35% de l''AET. Il est déconseillé de faire moins de 30% car un apport adéquat en AG (insat surtout) serait difficile à couvrir', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('91e23fbec5d6dbfa', E'Le glycogène est transformé en acide lactique à mesure que s''installe la rigidité cadavérique (i.e. transfo muscle => viande) d''où l''absence de glycogène dans les viandes et les produits de la pêche', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('356e49b78f6e9788', E'Lipides et protéines diminuent la réponse glycémique postprandiale', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('dfc95d0e3b050192', E'Les fibres solubles, au contact de l''eau, forment un gel visqueux qui :\n- ralentit le transit intestinal\n- forme un complexe avec les glucides, réduisant leur biodispo -> diminution glycémie postprandial', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('6309ce551a191d9c', E'Les acides gras saturés sont apportés par l''alimentation ou sont synthétisés dans le foie, le cerveau et le tissu adipeux où ils constituent les sphingolipides et les triglycérides de réserve.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('c9560f90054b0e63', E'Les acides gras polyinsaturés sont appelés acides gras essentiels car ils ne sont pas synthétisables par l''humain.\nLes plus connus sont :\n- le linoléique, le gamma-linoléique et l''acide arachidonique, dans le groupe des oméga 6\n- l''alpha-linoléique, l''acide eicosapentaénoïque (EPA) et de l''acide docosahexaénoïque (DHA), dans els groupe des oméga 3\n', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('9423c221e1eb5219', E'L''acide linoléique et l''acide alpha-linoléique en plus d''être AGE sont des acides gras indispensables car:\n- ils sont les seuls présents en quantité significative dans els aliments\n- ils sont précurseurs des autres AGE grâce à des réactions de désaturation et d''élongation\nIls doivent donc être régulièrement apportés par l''alimentation.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('809aeee23dd707e0', E'AGS à chaîne courte\n---------------------\n- Neutre vis-à-vis des maladies cardiovasculaires.\n- L''acide butyrique (4 C) est un inhibiteur de la prolifération cellulaire. Il participe à la prévention du cancer du colon.\n\nAGS à chaîne moyenne\n-----------------------\n- En excès ils sont athérogènes, thrombogènes et hypercholestérolémiants\n- Pas d''incidendes particulières sur le cancer\n- Ils assurent une part improtante de la dépense énergétique et interviennent dans la structure membranaire de certaines cellules nerveuses (myéline)\n\nAGS à chaîne longue\n---------------------\n- En excès ils sont très athérogènes, très thrombogènes et très hypercholestérolémiants\n- Pas d''incidendes particulières sur le cancer\n\nAGMI\n------\n- Antiathérogènes, Antithrombogènes et hypocholestérolémiants\n- Pas d''incidendes particulières sur le cancer\n- Ils sont source d''énergie, maintiennent le tissu adipeux à l''état fluide à la temp. du coprps et ils modulent l''activité de certaines molécules (enzymes, transporteurs, récepteurs, ...)\n\nAGPI (6)\n---------\n- Ils sont hypocholestérolémiants, hypotriglycéridémiants, empêchent le dysfonctionnement plaquettaire\n- En excès, l''acide linoléique favoriserait la croissance tumorale et la formation des métastases tandique que l''acide arachidonique favoriserait la tumorigénèse par certaines molécules dont il est la source (en particulier les prostaglandines E2)\n- Ils interviennent dans la fonction reproductrice, participent au maintien de l''intégrité de l''épiderme et sont impliqués dans l''activité du système immunitaire et la réponse inflammatoire en tant que précurseurs de nombreuses molécules biologiquement actives\n\nAGPI (3)\n---------\n- l''acide al-linoléique inhibe l''agrégation plaquettaire et donc favorise la fluidité du sang, prévient la fibrillaiton vasculaire et améliore la fonction contractile du coeur, réduit le risque coronarien et prévient la mort subite. L4EPA et le DHA sont hyptrygl. et stabilisent les membranes cellulaires. Les oméga 3 augmentent le HDL-chol. Ils ont une action hypotensive chez les individus hypertendus.\n- Ils s''opposeraient à la croissance tumorale en limitant la synthèse de l''acide arachidonique et en diminuant la fréquence de certains cancers (sein, côlon et prostate).\n- Ils interviennent dans la constitution des membranes cellulaires et participent ainsi au dev et à la physiologie du syst nerveux. Le lait maternel et riche en oméga3', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('28b65d1e698e964f', E'Le cholestérol a comme fonctions\n- précuseur des hormones sexuelles (progestérone, oestrogène et testostérone) et des corticoïdes\n- précurseur des sels biliaires\n- précusreur de la vitamine D3\n- cosntituant des membranes cellulaires (étanchéité, fluidité)', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('e3032966d3cf00a3', E'HDL et LDL sont des lipoprotéines', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('3d5b5e8820db2699', E'Les fibres solubles (fruits et légumes) sont les plus intéressants dans les préventions des maladies cardiovasculaires :\n- elles augmentent l''élimination des sels biliaires => diminution de la réabsorption du cholestérol\n- elles sont fermentées au niveau du côlon et produisent des AGs volatils qui auraient la propriété de réduire le LDL-chol', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('fdbc81c47043148e', E'Les fibres insolubles (céréales, légumes secs) :\n- accélèrent le transit\n- diminuent l''assmilation des lipides en augmentant l''excrétion fécale des sels biliaires', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('740593f88b905ae9', E'Les fibres limitent l''effet hyperglycémiant des glucides en diminuant l''action des enzymes digestives.', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('5502379348959cad', E'Les fibres solubles sont :\n- non digestibles\n- fermentescibles\n- satiétogènes\n- hydrosolubles', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('2143a88218474974', E'Les fibres solubles sont fermentescibles => dégradées par les enzymes de la flore colique. Leur digestion conduit à la formation :\n- d''AG volatiles à chaînes courtes qui seront réabsorbés, éliminés ou réutilisés par la flore. Ces AG ont des vertues :\n   o protectrices contre certains cancers\n   o immunitaires\n   o hypocholestérolémiants en réduisant le LDL\n- de gaz', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('9df3f2540a13e2b8', E'La plupart des vitamines sont absorbées au niveau de l''intestin grêle, principalement au niveau du duodénum et du jéjunum', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('afdb7c24962efa52', E'Seules les vitamines A (1 à 2 ans), D, E et B12 (3 à 5 ans) sont stockées dans l''organisme (foie, tissu adipeux et muscles).', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('0f0edaaab75373d2', E'Sensibilités des vitamines:\nA   : C(++), L(+++), O(+++), R(-), H(-), A(++), B(-)\nD   : C(++), L(+++), O(+++), R(-), H(-), A(++), B(++)\nE    : C(++), L(++), O(++), R(-), H(-), A(-), B(++)\nK    : C(-), L(+++), O(++), R(-), H(-), A(-), B(++)\nB1  : C(+++), L(++), O(-), R(++), H(++), A(-), B(+++)\nB2  : C(-), L(+++), O(-), R(++), H(-), A(-), B(+++)\nPP  : C(-), L(-), O(-), R(++), H(-), A(-), B(-)\nB5  : C(++), L(-), O(-), R(-), H(++), A(+++), B(+++)\nB6  : C(-), L(++), O(-), R(-), H(-), A(++), B(++)\nB8  : C(-), L(++), O(-), R(-), H(-), A(++), B(++)\nB9  : C(-), L(++), O(+++), R(+++), H(-), A(++), B(++)\nB12 : C(-), L(++), O(-), R(+++), H(++), A(+++), B(+++)\nC     : C(++), L(++), O(+++), R(-), H(++), A(++), B(+++)\n\n-       -> stable\n++    -> sensible\n+++ -> très sensible\n\nC -> chaleur\nL -> lumière\nO -> oxydants\nR -> réducteurs\nH -> humidité\nA -> acides\nB -> bases', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('ad6c8fd7020ac070', E'Le cerebellum est organisé de façon topographique :\n- au milieu, le vermis, qui va contrôler tout ce qui est central (posture, démarche et parole)\n- le paravermis, contrôle le mouvement des bras\n- les lobes latéreaux, sont ""appariés"" au pons et contrôlent on ne sait pas trop quoi (pê l''apprentissage de mouvements ou d''autres choses)\n- le flocculus et un bout du vermis gère le VOR et pour la poursuite oculair', 'null', 'null', (SELECT id from note_source where uri = 'b251937635d1af38'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('4f24b393b3d33547', E'Le cerebellum reçoit une copie des données moteurs envoyés du cortex moteur primaire aux neurones moteurs mais aussi des messages reçus par les neurones moteurs.\nIl reçoit aussi les messages de reafference sensorielle provenant de la peau et des tendons.\n\nLorsque la copie efférente et la reafference sensorielle ne correspondent pas alors le cervelet opère une correction de trajectoire. C''est de l''apprentissage associatif', 'null', 'null', (SELECT id from note_source where uri = 'b251937635d1af38'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('a4fb9c1cb2879ac1', E'Möchten\n-----------\nich möchte\ndu möchtest\ner/sie/es möchte\n\nwir möchten\nihr möchtet\nSie möchten', 'null', 'null', (SELECT id from note_source where uri = 'b5b7716ff8a06a6a'));
