insert into note (uri,valeur,mime_type,file_id,source_id) values ('f7bb2b11e57b00f6', E'Sources d''antioxydants\n-----------------------\nVitamine E\n-----------\n- Huiles mélangées\n              tournesol\n              pépins de raisins\n              germes de maïs\nMargarines végétales au tournesol et au maïs\nGraines oléagineuses (amandes et noisettes)\n\nBétacarotène ou provitamine A\n-------------------------------\n-Végétaux rouge orangé (abricot, melon, pêche, tomate,...)\n- Végétaux verts (épinards, certains choux et salades, poireaux, asperges, courgette, haricots verts,...)\n\nVitamine C\n-----------\n- Fruits frais (kiwi, agrumes, fraise, melon, brugnon,...)\n- Légumes frais (chou de Bruxelles, vert, poivron,...)\n\nFlavonoïdes\n------------\n- Oignon\n- Raisin\n- Myrtille\n- Thé\n- Café\n\nSélénium\n---------\n- Ail\n- Céréales complètes\n- Végétaux en général\n\nZinc\n----\n- Jaune d''œuf\n- Céréales complètes', 'null', 'null', (SELECT id from note_source where uri = '1819d5f7d7600d47'));
