insert into note (uri,valeur,mime_type,file_id,source_id) values ('7fd712bf3e11733d', E'Les protéines des légumineuses sont pauvres en acides aminés soufrés (cystine et méthionine)', 'null', 'null', (SELECT id from note_source where uri = '6520238c47b9c015'));
