insert into note (uri,valeur,mime_type,file_id,source_id) values ('e46e6d1fa62e495c', E'Nuclei in the midbrain provide important linkages between components of the motor system, particularly the cerebellum, basal ganglia, and cerebral hemispheres.\nThe midbrain also contains components of the auditory and visual systems. Finally, several regions of the midbrain give\nrise to pathways that are connected to the extraocular muscles that control eye movements.', 'null', 'null', (SELECT id from note_source where uri = '9f9a616c1e9c3771'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('53f8e508e3d1a4a4', E'Couches du neocortex :\n1 - moléculaire (dendrites des neurones du dessous + axones vers d''autres régions)\n2 - External granule cell\n3 - External pyramidal cell\n4 - Internal granule cell\n5 - Internal pyramidal cell\n6 - Multiform\n\n\n\nIV - It is the main recipient of sensory input from the\nthalamus and is most prominent in primary sensory\nareas.', 'null', 'null', (SELECT id from note_source where uri = '9f9a616c1e9c3771'));
