insert into note (uri,valeur,mime_type,file_id,source_id) values ('86ec2fb15dc212d2', E'Trop d''images, de sons et de sollicitations diverses semblent générer des troubles de l''attention et de la concentration, des syndrome d''hiperactividad et de comportements d''addiction', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('3a597bf0aecc7fb2', E'La melatonine est appelée hormone du sommeil. Elle est généralement sécrétée et absence de lumière. Elle régule pas mal de fonctions du corps qui doivent se passer la nuit. Elle est associée à un sommeil précoce, long et de bonne qualité. Elle dérive de la sérotonine ou peut être rendue disponible par l''alimentation avec notamment les noix et les noisettes. ', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('55ade2bdc1356289', E'Le cerveau désactive ses réseaux émotionnels lorsqu''il est soumis à des images répétées de violence', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('b3a8a14518538b29', E'Avec Kant ce n''est plus le figure divine de l''absolu, de l''omniscience, qui vient relativiser la finitude humaine, la définir comme moindre être. Tout à l''inverse, c''est au nom de la finitude indépassable qui est celle de toute connaissance humaine, que je figure divine de l''absolu est à son tour relativisée, rabaissée au rang d''une simple ""Idé" dont je réalité objective est désormais indémontrable par les voies d''une quelconque théorie philosophique ou scientifiqu', 'null', 'null', (SELECT id from note_source where uri = 'dec768f65b6745e6'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('dbec7eba5f963817', E'Par opposition au concept, l''intuition se définit comme une ""représentation singulièr", i.e. une représentation par laquelle je saisis l''existence même, l''individu réel. Elle est toujours liée à la sensibilité, tjrs située dans un espace et un temps.', 'null', 'null', (SELECT id from note_source where uri = 'dec768f65b6745e6'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('6111a7646295226b', E'Objectif hypothétique du tout numérique à l''école : en remplaçant l''humain par le numérique on habitue peu à peu les gens aux baisers d''effectifs des profs et des moyens éducatifs', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('d918fd8340159163', E'Le manque de sommeil semble être lié à un risque accru d''obésité, de pensées suicidaires, de retards de langage et bien d''autres joyeusetés', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('f45f4f8eb62e15c6', E'Une association entre un concept et le plaisir peut être créé au niveau du cerveau (cortex orbitofrontal median) et lors de la preception de ce concept le plaisir arrive. Par exemple, si on associe coca/bon alors un verre estampillé ""coca"" aura notre préférence sur toute autre option même si on avait déjà identifié que le coca n''est pas notre boisson préféré en test aveugl', 'null', 'null', (SELECT id from note_source where uri = '847f48fd9bb20a48'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('747fe4d148ea3f69', E'L''idée serait la connaissance du particulier par concept et non par intuition. L''idée serait le concept si le concept pouvait déduire son extension par sa compréhension.', 'null', 'null', (SELECT id from note_source where uri = 'dec768f65b6745e6'));
insert into note (uri,valeur,mime_type,file_id,source_id) values ('20a4b4b8cec1e14f', E'Pour Leibniz, les relations entre les êtres que l''entendement humain borné perçoit à travers les sens sont en soi (i.e. pour un être absolu divin) des relations purement intelligibles', 'null', 'null', (SELECT id from note_source where uri = 'dec768f65b6745e6'));
